import Input from "../../UI/Input";
import "./MealItemForm.css";

function MealItemForm(props) {
  return (
    <form className="form">
      <Input
        lable="Amount"
        Input={{
          id: "amount",
          min: "1",
          max: "5",
          type: "number",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button>+ Add</button>
    </form>
  );
}
export default MealItemForm;
