import "./MealItem.css";
import MealItemForm from "./MealItemForm";
function MealItem(props) {
    const price=props.price.toFixed(2);
  return (
    <li className="meal">
      <div>
          <h3>{props.name}</h3>
          <div className="description ">{props.desc}</div>
          <div className="price">{'$ ' + price}</div>
      </div>
      <div>
          <MealItemForm/>
      </div>
    </li>
  );
}
export default MealItem;
