import CartContext from "./cart-context";
import {useReducer} from 'react';

    const actionType={
        add:'Add',
        remove:'remove',
    }


 const defaultCartItem={
     itemList:[],
     totalAmount:0,
 }

 function CartReducer(state,action) {   
    if(action===actionType.add){
        const updatedItem=state.itemList.concat(action.dataItem);
        const updateAmount=state.totalAmount+action.dataItem.price*action.dataItem.amouunt;
    }
    return defaultCartItem;
}



function CartProvider(props){

  const [cartState,dispatchCart]=useReducer(CartReducer,defaultCartItem)

    function addItemToCart(item){
        dispatchCart({type: actionType.add,dataItem:item})
    }

    function removeItemToCart(id){
        dispatchCart({type: actionType.remove,dataId:id})
    }


    const cartContext={  
        items:cartState.itemList,
        totalAmount: cartState.totalAmount,
        addItem: addItemToCart,
        removeItem: removeItemToCart,
    }


    return(
        <CartContext.Provider value={cartContext}>
            {props.children}
        </CartContext.Provider>
    );
}
export default CartProvider;
